#include <iostream>using namespace std;///Ingresar dos fechas en partes separadas (dia, mes y año)///Se ingresa de ese modo la fecha actual y la de nacimiento.///Calcular la edad utilizando una funcion.int Edad(int x, int y);int main(int argc, char *argv[]) {	int d1, d2, m1, m2, a1, a2;		cout<<"INGRESE FECHA ACTUAL: "<<endl;	cin>>d1>>m1>>a1;	cout<<endl;	cout<<"INGRESE FECHA DE NACIMIENTO: "<<endl;	cin>>d2>>m2>>a2;		int actual = a1*10000+m1*100+d1;	int nac = a2*1000+m2*100+d2;		cout<<"La edad de la persona es: "<<Edad(actual,nac);	return 0;}int Edad(int x, int y){	int age = (x-y)/1000000;	return age;}
	
